  `score_logical_thinking` decimal(5,2) DEFAULT NULL COMMENT '核心能力-逻辑思维能力得分',
  `score_innovation` decimal(5,2) DEFAULT NULL COMMENT '核心能力-创新能力得分',
  `score_stress_resistance` decimal(5,2) DEFAULT NULL COMMENT '核心能力-应变抗压能力得分',
  `behavioral_analysis` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '面试行为表现分析总结',
