DROP TABLE IF EXISTS `experience_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `experience_posts` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '面经唯一ID',
  `user_id` bigint unsigned NOT NULL COMMENT '作者的用户ID (逻辑关联 users.id)',
  `session_id` varchar(255) COLLATE utf8mb4_general_ci NOT NULL COMMENT '关联的面试会话ID (逻辑关联 interview_sessions.id)',
  `title` varchar(255) COLLATE utf8mb4_general_ci NOT NULL COMMENT '面经标题',
  `content` mediumtext COLLATE utf8mb4_general_ci NOT NULL COMMENT '用户撰写的复盘内容 (支持富文本)',
  `visibility` enum('PUBLIC','PRIVATE') COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'PUBLIC' COMMENT '可见性 (PUBLIC:公开, PRIVATE:私有)',
  `is_anonymous` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否匿名发布 (0:否, 1:是)',
  `status` varchar(255) COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'PUBLISHED' COMMENT '状态 (PUBLISHED:已发布, UNDER_REVIEW:审核中, HIDDEN:已隐藏)',
  `is_featured` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否为精华帖 (0:否, 1:是)',
  `views_count` int unsigned NOT NULL DEFAULT '0' COMMENT '浏览量',
  `likes_count` int unsigned NOT NULL DEFAULT '0' COMMENT '点赞数',
  `comments_count` int unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `collections_count` int unsigned NOT NULL DEFAULT '0' COMMENT '收藏数',
  `shared_report_elements` json DEFAULT NULL COMMENT '选择性分享的报告元素, 如: {"show_radar": true, "show_score": false}',
  `tags` text COLLATE utf8mb4_general_ci COMMENT '标签列表：["标签1","标签2"]',
  `experience_url` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '面经的封面链接',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_status_visibility_counts` (`status`,`visibility`,`likes_count`,`comments_count`) COMMENT '用于排序和筛选',
  KEY `idx_user_id` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=26 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci ROW_FORMAT=DYNAMIC COMMENT='面经分享主表';
/*!40101 SET character_set_client = @saved_cs_client */;

