  KEY `idx_session_id_start_time` (`session_id`,`start_time_seconds`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='存储面试过程中每30秒一次的详细行为分析数据块';
