DROP TABLE IF EXISTS `interview_channels`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `interview_channels` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '频道唯一ID',
  `creator_id` bigint unsigned DEFAULT NULL COMMENT '创建者ID（NULL表示为官方公开频道）',
  `title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '频道标题',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '频道描述',
  `major` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '专业方向',
  `job_type` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '不限' COMMENT '工作类型(''校招'',''社招'',''实习'',''兼职'',''不限'')',
  `target_position` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '应聘岗位(如后端）',
  `interviewer_style` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '随机' COMMENT '面试官风格(''温和'',''严肃'',''技术型'',''压力面'',''行为面'',''随机'',''自定义'')',
  `target_company` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '应聘公司',
  `interview_mode` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '一对一' COMMENT '面试形式(''一对一'', ''群面'', ''多对一'')',
  `estimated_duration` int DEFAULT NULL COMMENT '预计面试时长（分钟）',
  `visibility` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '私有' COMMENT '可见性(''公开'',''审核中‘, ''私有'',''已拒绝'')-用户创建频道时为私有，选择公开则通过管理员审核，审核完为公开状态',
  `is_deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否被删除（软删除）',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `image_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '频道封面URL',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `usage_count` int DEFAULT '0' COMMENT '频道使用次数-表示受欢迎程度，可以根据这个排序',
  PRIMARY KEY (`id`),
  KEY `idx_creator_id` (`creator_id`)
) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='存储面试的配置模板，即“频道”';
/*!40101 SET character_set_client = @saved_cs_client */;

