DROP TABLE IF EXISTS `interview_dialogue`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `interview_dialogue` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '对话记录ID',
  `session_id` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '所属面试会话ID',
  `sequence` int unsigned NOT NULL COMMENT '对话顺序（如1、2、3）',
  `ai_message` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT 'ai提问的问题或回答（因为用户可能会问面试官问题）',
  `user_message` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '用户回答的内容或提问的问题',
  `timestamp` datetime(3) NOT NULL COMMENT '消息时间戳（精确到毫秒）',
  `turn_analysis` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '该轮对话的分析(如：用户在回答这道题时，包含：1.眼神分析 2.情绪分析 3.回答与正确内容的分析【眼神飘忽不定,答案不尽人意....】)',
  `turn_score` int DEFAULT NULL COMMENT '改论对话的分数（0-100）',
  `turn_suggestion` text COLLATE utf8mb4_unicode_ci COMMENT '该论对话的建议',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_session_id` (`session_id`)
) ENGINE=InnoDB AUTO_INCREMENT=233 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='存储面试过程中的每一轮对话及初步分析';
